namespace commata {
  class text_error : public std::exception {
  public:
    static constexpr std::size_t npos = -1;

    // , constructors and assignment operators:
    template <class T> explicit text_error(T&& what_arg);
    text_error(const text_error& other) noexcept;
    text_error(text_error&& other) noexcept;
    text_error& operator=(const text_error& other) noexcept;
    text_error& operator=(text_error&& other) noexcept;

    const char* what() const noexcept override;

    // , physical postion information:
    text_error& set_physical_position(std::size_t row = npos, std::size_t col = npos);
    const std::pair<std::size_t, std::size_t>* get_physical_position() const noexcept;

    text_error_info info(std::size_t base = 1) const noexcept {
      return text_error_info(*this, base);
    }
  };
}

namespace commata {
  class text_error_info {
  public:
    text_error_info(const text_error& ex, std::size_t base) noexcept;
    text_error_info(const text_error_info& other) noexcept;
    text_error_info& operator=(const text_error_info& other) noexcept;

    const text_error& error() const noexcept;
    std::size_t get_base() const noexcept;

  private:
    const text_error* e;  // exposition only
    std::size_t b;        // exposition only
  };
}

