                                     BelowLowerLimit&& on_below_lower_limit = BelowLowerLimit(),
                                     Underflow&&       on_underflow         = Underflow());
    replace_if_conversion_failed(const replace_if_conversion_failed& other);
    replace_if_conversion_failed(replace_if_conversion_failed&& other) noexcept;
   ~replace_if_conversion_failed();

    template <class Ch> replacement<T> invalid_format(const Ch*, const Ch*) const;
    template <class Ch> replacement<T> out_of_range(const Ch*, const Ch*, int sign) const;
                        replacement<T> empty() const;
  };
